import React, { useRef, useState } from "react";
import { FiCloud, FiX } from "react-icons/fi";
import "./UploadPage.css";

export default function UploadPage() {
  const inputRef = useRef(null);
  const [files, setFiles] = useState([]);

  function handleBrowse() {
    inputRef.current.click();
  }

  function handleSelected(e) {
    const picked = Array.from(e.target.files);
    setFiles((prev) => [...prev, ...picked]);
  }

  return (
    <div className="uploadBg">
      <div className="card">
        {/* header */}
        <div className="header">
          <FiCloud size={32} />
          <div className="hdrText">
            <h2>Upload files</h2>
            <p>Select and upload the files of your choice</p>
          </div>
          <FiX size={20} className="close" />
        </div>

        {/* drop zone */}
        <label className="dropzone">
          <input
            ref={inputRef}
            type="file"
            multiple
            onChange={handleSelected}
            hidden
          />
          <FiCloud size={48} />
          <span className="dzMain">Choose a file or drag & drop it here</span>
          <span className="dzSub">
            JPEG, PNG, PDF, and MP4 formats, up to 50 MB
          </span>
        </label>

        <button className="browseBtn" onClick={handleBrowse}>
          Browse File
        </button>

        {/* simple file list */}
        {files.length > 0 && (
          <div className="fileList">
            {files.map((f, i) => (
              <div className="fileRow" key={i}>
                <span className="fileName">{f.name}</span>
                <span className="fileSize">
                  {(f.size / 1024).toFixed(1)} KB
                </span>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
}
