import React from 'react';
import { Nav } from 'react-bootstrap';
import { FiBarChart2, FiLayers, FiFileText, FiShuffle, FiBell } from 'react-icons/fi';
import './SideNav.css';      // put icon hover colour here if you like

export default function SideNav({ active = 'Overview' }) {
  const items = [
    { key: 'Overview',   icon: <FiBarChart2 /> },
    { key: 'Expenses',   icon: <FiLayers /> },
    { key: 'Reports',    icon: <FiFileText /> },
    { key: 'Transfers',  icon: <FiShuffle /> },
    { key: 'Notifications', icon: <FiBell /> },
  ];

  return (
    <div className="sidenav d-flex flex-column py-4 px-3">
      <h5 className="mb-4 fw-bold">MYFINANCES</h5>

      <Nav className="flex-column gap-1">
        {items.map(({ key, icon }) => (
          <Nav.Link key={key} active={key === active} className="d-flex align-items-center gap-2">
            {icon} {key}
          </Nav.Link>
        ))}
      </Nav>
    </div>
  );
}
