// src/components/Banner.js
import React from 'react';
import { Carousel } from 'react-bootstrap';
import ReactPlayer from 'react-player';

const Banner = () => (
  <Carousel className="mb-5">
    {/* Slide 1: static image */}
    <Carousel.Item>
      <img
        className="d-block w-100"
        src="/assets/uber-hero1.jpg"
        alt="Uber data overview"
        style={{ maxHeight: '400px', objectFit: 'cover' }}
      />
      <Carousel.Caption>
        <h3>Visualize Your Rides</h3>
        <p>Get instant insights into your trip patterns</p>
      </Carousel.Caption>
    </Carousel.Item>

    {/* Slide 2: looping video */}
    <Carousel.Item>
      <div style={{ position: 'relative', paddingTop: '40%' /* aspect ratio */}
      }>
        <ReactPlayer
          url="/assets/uber.mov"
          playing
          loop
          muted
          width="100%"
          height="100%"
          style={{ position: 'absolute', top:0, left:0, objectFit:'cover' }}
        />
      </div>
      <Carousel.Caption>
        <h3>Watch the Dashboard in Action</h3>
      </Carousel.Caption>
    </Carousel.Item>

    {/* Slide 3: another image */}
    <Carousel.Item>
      <img
        className="d-block w-100"
        src="/assets/uber-hero2.jpg"
        alt="Charts preview"
        style={{ maxHeight: '400px', objectFit: 'cover' }}
      />
      <Carousel.Caption>
        <h3>Switch Between Charts</h3>
        <p>All your key metrics at the click of a button</p>
      </Carousel.Caption>
    </Carousel.Item>
  </Carousel>
);

export default Banner;
