import React from 'react';
import { Line } from 'react-chartjs-2';
import { Chart as ChartJS, CategoryScale, LinearScale, LineElement, PointElement } from 'chart.js';

ChartJS.register(CategoryScale, LinearScale, LineElement, PointElement);

export default function StatMini({ label, value, spark }) {
  const chartData = {
    labels: spark.map((_, i) => i),
    datasets: [{
      data: spark,
      tension: 0.4,
      borderWidth: 2,
      pointRadius: 0,
    }],
  };
  const chartOpt = { plugins: { legend: { display: false } }, scales: {x:{display:false},y:{display:false}} };

  return (
    <div className="d-flex flex-column align-items-center">
      <small className="text-uppercase fw-semibold">{label}</small>
      <h6 className="fw-bold mb-1">{value}</h6>
      <div style={{ width: 60, height: 20 }}>
        <Line data={chartData} options={chartOpt} />
      </div>
    </div>
  );
}
