import React from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { Container, Button } from 'react-bootstrap';
import Charts from './components/Charts';

export default function ChartsPage() {
  const { state } = useLocation();        // data passed from UploadPage
  const navigate = useNavigate();

  // user refreshes or lands directly on /charts
  if (!state?.data) {
    return (
      <Container className="py-5 text-center">
        <p className="lead mb-4">No data found. Please upload a CSV first.</p>
        <Button onClick={() => navigate('/upload')}>Go to Upload</Button>
      </Container>
    );
  }

  return (
    <Container className="py-4">
      <Charts data={state.data} />
    </Container>
  );
}
